package _05_for_loops._2_badgers;

public class Badgers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		for (int reps1 = 0;reps1 < 2; reps1 += 1) {
			
			
			
			for(int reps = 0;reps <= 11; reps += 1) {
				System.out.print("Badger");
				if(reps < 11) {
					System.out.print(", ");
				}
				
			}
			System.out.println();
			
			for (int reps2 = 0; reps2 < 2; reps2 += 1) {
				System.out.print("Mushroom");
				if (reps2 == 0) {
					System.out.print(", ");
				}
				
			}
			System.out.println();
			System.out.println();
			
			
			
		
		}
		
		System.out.println("A snake!!!!!!!!!!!!!!!!!!!!!!");
	}

}
